function vveShowMessages(dataObj){clearInfoMsg();if(typeof(dataObj.infomsg)=='object'){for(var i=0;i<dataObj.infomsg.length;i++){infoMsg(dataObj.infomsg[i],false);}}else if(typeof(dataObj.infomsg)=='string'){infoMsg(dataObj.infomsg);}
clearErrMsg();if(typeof(dataObj.errmsg)=='object'){for(var y=0;y<dataObj.errmsg.length;y++){errMsg(dataObj.errmsg[y],false);}}else if(typeof(dataObj.infomsg)=='string'){errMsg(dataObj.errmsg);}}
function infoMsg(msg,clear){if(msg==null)return;if(typeof(clear)=='undefined')clear=true;if(clear==true){clearInfoMsg();}
$('#infoMessages').prepend("<p>"+msg+"</p>").show();}
function clearInfoMsg(){$('#infoMessages').html(null).hide();}
function clearErrMsg(){$('#errMessages').html(null).hide();}
function errMsg(msg,clear){if(msg==null)return;if(typeof(clear)=='undefined')clear=true;if(clear==true){$('#errMessages').html(null);}
$('#errMessages').prepend("<p>"+msg+"</p>").show();}
function showLoadBox(box,timeout){if(typeof(timeout)=="undefined"){timeout=5000;}
var jbox=$(box);var overBox=$('<div id="loadingBox" style="background-color: white; text-align:center;"><img src="images/progress.gif" alt="Loading..." /></div>').css({position:'absolute',top:0,left:0,opacity:0,width:jbox.width(),height:jbox.height()});var parentBox=$(box).parent('div,p');parentBox.css('position','relative').prepend(overBox.hide());parentBox.children('#loadingBox').fadeTo(500,0.5);setTimeout("hideLoadBox()",timeout);}
function hideLoadBox(){$('#loadingBox').fadeOut(500,function(){$('#loadingBox').remove();})}
function vveLoadImage(src,callback){var cacheImage=document.createElement('img');cacheImage.onload=function(){if($.isFunction(callback)){callback.call(cacheImage);}}
cacheImage.src=src;}
$(document).ready(function(){$("div.toolbox").parent('div').css({position:'relative'});$("a.toolbox-button").click(function(){var $toolbox=$(this).css({'z-index':1}).next('div.toolbox').clone(true);$('body').append($toolbox);$toolbox.css({opacity:1,top:$(this).offset().top,left:$(this).offset().left-$toolbox.width()+16,width:$toolbox.width()}).show().mousemove();return false;});$("a.toolbox-button").hover(function(){$(this).css({'z-index':3}).addClass('toolbox-button-hover').parent().addClass('toolbox-active-content');},function(){if($('body>div.toolbox').length==0){$(this).css({'z-index':1}).removeClass('toolbox-button-hover').parent().removeClass('toolbox-active-content');}});$("div.toolbox").hover(function(){$(this).css({'z-index':10000,opacity:1}).show();},function(){$('.toolbox-active-content').removeClass('toolbox-active-content');$(this).animate({opacity:0},500,function(){$(this).remove();});});});